"use strict";(self.webpackChunkfrontend=self.webpackChunkfrontend||[]).push([[382],{8980:(a,e,p)=>{p.d(e,{S:()=>A});const r=p.p+"5e5786d894b3cc749b8f55edb348602a.png",t=p.p+"b8ee1ac5ed2d013b4e1a2ef27ff82713.png",i=p.p+"880dcade2fbe9dd2bd8ff4ee1c73ea05.png",T=p.p+"c0377924a2e84bdc4e744e8297ae3f73.png",g=p.p+"8d8f98e49b84a429f1aa44a2966b3767.png",s=p.p+"21a2010b181c209794c2aecd2155b727.png",c=p.p+"b5dfd1e80be8be3d2b73d06ef3890443.png",l=p.p+"62684c42dd1d96cf2258bc0c444ac092.png",h=p.p+"089f2dd7aff069694bac705053af3333.png",m=p.p+"d706a3e0353b11be16afdd00e6b7b396.png",E=p.p+"06923ac46eedf035683716e0cce359cf.png",y=p.p+"5961898254229eece61b2924206f6602.png",b=p.p+"b8e118bc19b5a33a6da569464fe35a8b.png",d=p.p+"79f4e71eb68d908b02e5adf012a8a9d3.png",f=p.p+"fd6ba06e5ba37b18107471a0f63bbc39.png",n=p.p+"e50525e4c98e12f4863f28032cbaf576.png",I=p.p+"8b182915552957645aad51ab1cb42433.png",L=p.p+"f47d8bd959ef93a400cf434c9efded1b.png";var A=[{idArticle:"1",title:"ФИБРОСКАНИРОВАНИЕ ПЕЧЕНИ — ЧТО ЗА ОБСЛЕДОВАНИЕ",subtitle:"Фибросканирование печени — инновационный, неинвазивный (без проникновения внутрь органа) метод определения стадии фиброза и степени стеатоза (наличия жировых клеток в печени) с высокой степенью достоверности результата, является признанным в мировом гепатологическом сообществе стандартом.\n",img:r,createdAt:"27.04.2024",blocks:[{type:"IMAGE",src:t,paragraphs:[]},{type:"TEXT",src:"",imageTitle:"",paragraphs:["Фибросканирование печени — инновационный, неинвазивный (без проникновения внутрь органа) метод определения стадии фиброза и степени стеатоза (наличия жировых клеток в печени) с высокой степенью достоверности результата. Он является признанным в мировом гепатологическом сообществе стандартом. Используемый аппарат в Центре — единственный в Казахстане аппарат экспертного класса, рекомендованный ведущими специалистами WFUMB и EFSUMB (Всемирной и Европейской федерации по ультразвуку в медицине и биологии), соответствует стандартам EASL (Европейской ассоциации по изучению печени)."]},{type:"TEXT",src:"",imageTitle:"",paragraphs:["Рекомендуется проходить обследование натощак. Воду пить можно, но только негазированную.","Голодный промежуток должен составлять не менее 4 часов."]},{type:"TEXT",src:"",imageTitle:"",paragraphs:["Проводится амбулаторно, занимает около 15 минут.","Обследование проводится, когда пациент лежит на кушетке, положив правую руку под голову. Врач проводит ультразвуковым датчиком аппарата по коже, исследуя нужные точки и зоны, при этом пациент не ощущает никакого дискомфорта или боли. Исследование безопасно для детей и взрослых и может проводиться многократно в течение года.","Результат обследования выдается на руки в течение 10–15 минут."]},{type:"TEXT",src:"",imageTitle:"",paragraphs:["Новейшее программное обеспечение, которым снабжен аппарат, детально фиксирует все важные показатели и выдает детализированное заключение. Предварительная запись на фибросканирование печени обязательна.","Запись на исследование осуществляется по телефонам с понедельника по пятницу с 08:00 до 17:00: 8(771)-103-70-02, 8(771)-103-70-03."]}]},{idArticle:"2",title:"Лечение вирусного гепатита В",subtitle:"Лечение вирусного гепатита В в нашем специализированном центре - это профессиональный подход к сложному и опасному заболеванию, которое пока неизлечимо. Противовирусная терапия при гепатите В не может убрать вирус из организма, но тем не менее может спасти жизнь, то есть сохранить печень и избежать цирроза и гепатоцеллюлярной карциномы.",img:i,createdAt:"29.04.2024",blocks:[{type:"IMAGE",src:T,paragraphs:[],imageTitle:"По данным всемирной организации здравоохранения (ВОЗ) у 25% пациентов после заражения вирусным гепатитом в течении 20 лет развивается циррооз печени. Ежегодно у 5% больных циррозом печени развивается рак печени. Вот почему так важно своевременно обратиться к врачу, пройти обследование и начать лечение."},{type:"TEXT",src:"",imageTitle:"",paragraphs:["Лечение вирусного гепатита В в нашем специализированном гепатологическом центре - это профессиональный подход к сложному и опасному заболеванию, которое пока неизлечимо. Противовирусная терапия при гепатите В не может убрать вирус из организма, но тем не менее может спасти жизнь, то есть сохранить печень и избежать цирроза и гепатоцеллюлярной карциномы."]},{type:"TEXT",src:"",imageTitle:"",paragraphs:["Лечение хронического вирусного гепатита В проводится противовирусными препаратами со следующими целями:","подавить активность вируса, то есть его размножение, ведущее к разрушению печеночной ткани и формирования цирроза.","препятствовать развитию цирроза и рака печени."]},{type:"TEXT",src:"",imageTitle:"",paragraphs:["Полное обследование вируса гепатита В: все иммуноферментные показатели, а также анализ методом ПЦР с вирусной нагрузкой.","Анализ на вирус дельта","Полное обследование печени: биохимические анализы, отражающие структурное и функциональное состояние печеночных клеток.","УЗИ брюшной полости с допплерографией, оценка степени фиброза (Эластометрия печени, ФиброМакс, фибротест). Обследование в нашем центре проводится на аппарате Фиброскан нового поколения","Клинический анализ крови.","Анализ на мутацию вируса В и устойчивость к препаратам."]},{type:"TEXT",src:"",imageTitle:"",paragraphs:["Для назначения противовирусной терапии необходимы серьезные основания. Поводом для начала терапии является высокий уровень ДНК HBV в крови, повышенная активность печеночных ферментов и умеренно или сильно выраженный фиброз (больше F2 по шкале МЕТАВИР).","Длительность терапии не может быть меньше, чем 5 лет. Если степень поражения печени близка к циррозу, или цирроз уже сформирован, терапия назначается пожизненно,. Противовирусные препараты представляют собой таблетки, которые хорошо переносятся и редко вызывают лекарственную устойчивость. Препараты обладают противофиброзным действием и в ряде случаев при терапии у пациента наблюдается обратное развитие фиброза.","Решение о назначении терапии должен принимать врач-гепатолог после соответствующего обследования. По рекомендациям Европейской ассоциации по изучению печени, все пациенты с циррозом печени должны получать лечение независимо от уровня виремии, также как и пациенты с семейным анамнезом (генетической, наследственной предрасположенностью) по раку печени и циррозу.","Пациентам с уровнем виремии больше 20 000 МЕ в мл крови и АЛТ в 2 раза превышающим норму лечение назначается независимо от степени фиброза."]},{type:"TEXT",src:"",imageTitle:"",paragraphs:["Если оснований для применения противовирусной терапии гепатита Б нет, то необходим регулярный контроль, не реже 1 раза в год. Под контролем должна быть вирусная нагрузка и состояние печени по биохимическим данным и степени фиброза по результатам эластометрии печени."]},{type:"TEXT",src:"",imageTitle:"",paragraphs:["Для лечения хронического гепатита В используются следующие препараты:","1. Интерферон альфа-2, пегилированные интерфероны.","2. аналоги нуклеозидов.","Установлена высокая эффективность препаратов интерферона альфа-2. Их терапевтический эффект объясняется иммуностимулирующим и противовирусным действием, что в совокупности помогает бороться с фиброзом.","Для терапии хронического гепатита B, в зависимости от активности воспалительного процесса в печени, используются разные схемы лечения. Их можно разделить на три категории: схемы с использованием высоких, средних и низких доз.","При хроническом течении вирусного гепатита В лечение начинают с высоких доз с дальнейшим переходом на режим низких доз. Курс «короткими» интерферонами осуществляется курсом 24-48 недель в режиме средних доз 5-6 млн МЕ подкожно ежедневно. Исчезновение вирусной ДНК и появление антител к антигену HBeAg при таком варианте лечения удается достич у 20-30%, а появление антител к HBsAg — в 10% случаев. Однако вероятность рецидива после окончания курса интерферонов чрезвычайно высока.","На фоне проведения курса лечения у ряда больных могут наблюдаться побочные эффекты: гриппоподобный синдром (лихорадка, озноб, головные боли, миалгии), диспептические расстройства, нарушения сна, слабость, снижение массы тела, лейкопения, тромбоцитопения. Большинство побочных эффектов являются дозозависимыми и могут быть устранены тщательным подбором дозы.","В настоящее время в клинической практике для лечения хронического вирусного гепатита B применяют пегилированные интерфероны. Они представляют собой соединение интерфрона с полиэтиленгликолем, позволяющим поддерживать в крови высокий уровень интерферона в крови на протяжении целой недели. Это делает препарат значительно более эффективным и удобным для применения.","Преимуществом лечения вирусного гепатита В пегилированными интерферонами является отсутствие лекарственной резистентности (устойчивости вируса гепатита В к применяемому препарату).","Пегилированный интерферон вводится один раз в неделю на протяжении 12 месяцев. Эффективность пегилированных интерферонов значительно выше, чем стандартного интерферона. Их применение приводит к более частому появлению антител к HBeAg и HBsAg и характеризуется меньшей частотой рецидива гепатита В после окончания курса лечения у HBe позитивных пациентов.","В последние годы появилось большое количество новых препаратов для лечения вирусного гепатит В с прямым противовирусным действием. Это аналоги нуклеозидов, применяемые в виде таблеток – ламивудин, адэфовир, энтекавир, телбувидин, тенофовир.","Аналоги нуклеозидов быстро и эффективно подавляют репликацию вируса гепатита В, при этом практически не имеют побочных действий. Однако, у ряда препаратов выявлена неприятная особенность: при длительном применении вирус гепатита В может мутировать и становиться устойчивым (резистентным) к данному препарату. Это делает применение лекарственного средства бесполезным. Немаловажно также, что препараты назначаются на длительный срок, часто неопределенно длительный (несколько лет) и их отмена может приводить к рецидиву заболевания или обострению вирусного гепатита В.","Было установлено, что длительный прием аналогов нуклеозидов в случае достижения авиремии (прекращения репликации вируса гепатита В) вызывает обратное развитие фиброза печени.","Врач-гепатолог нашего специализированного гепатологического центра определяет тактику лечения вирусного гепатита В. Поскольку нет единого стандарта принятия решений, тактика полностью зависит от состояния печени и активности вируса. Первичные пациенты получают в нашем центре консультацию гепатолога с целью назначения соответствующего обследования.","На этой консультации вы получите ответы на все вопросы по поводу заболевания, методов лечения, смысла обследования и эпидемической опасности для окружающих, а также о возможности и необходимости прививок от гепатита В для окружающих с целью предотвратить их заражение опасным вирусом. Вам назначат необходимое обследование на вирусный гепатит, по результатам которого будут рекомендованы или противовирусные препараты, или контроль без противовирусного лечения.","Гепатит В – очень коварная болезнь. Она протекает без симптомов, поэтому часто обращаются к врачу с впервые выявленным заболеванием на стадии цирроза, когда полное восстановление уже маловероятно. Поэтому главное условие успешного лечения вирусного гепатита В – контролировать состояние печени и активности вируса путем регулярного обследования. Необходимо посещать гепатолога в процессе лечения и контроля.","Запись на исследование осуществляется по телефонам с понедельника по пятницу с 08:00 до 17:00: 8(771)-103-70-02, 8(771)-103-70-03."]}]},{idArticle:"3",title:"ЛЕЧЕНИЕ ЗАБОЛЕВАНИЙ ПЕЧЕНИ: У ВЗРОСЛЫХ И ДЕТЕЙ",subtitle:"Заболевания печени являются одной из основных причин развития жизниугрожающих состояний, которые могут привести к инвалидизации и гибели человека.",img:g,createdAt:"30.04.2024",blocks:[{type:"TITLE",src:"",imageTitle:"",paragraphs:["Распространенность"]},{type:"IMAGEBLOCK",src:E,imageTitle:"",paragraphs:["1. Глобальная распространенность среди детей: 3–10% в общей популяции, 40–70% среди детей с ожирением","2. Чаще всего случаев с НАЖБП встречались у латиноамериканской и азиатской расы.","3. НАЖБП чаще встречается у мальчиков, чем у девочек."]},{type:"TITLE",src:"",imageTitle:"",paragraphs:["Факторы риска: что влияет на развитие метаболических нарушений?"]},{type:"TEXT",src:"",imageTitle:"",paragraphs:["Метаболические нарушения и заболевания печени могут развиваться под воздействием различных факторов риска. Понимание этих факторов помогает вовремя принять меры профилактики и снизить вероятность серьезных осложнений.","1. Ожирение. Избыточный вес, особенно абдоминальное ожирение, является одним из главных факторов риска. Жировая ткань влияет на метаболизм, способствуя развитию инсулинорезистентности и воспалительных процессов, что повышает вероятность неалкогольной жировой болезни печени (НАЖБП) и метаболического синдрома.","2. Малоподвижный образ жизни.Недостаток физической активности замедляет обмен веществ, способствует накоплению жира в печени и снижает чувствительность клеток к инсулину. Регулярные физические нагрузки помогают нормализовать вес, улучшить углеводный обмен и снизить риск метаболических нарушений.","3. Рацион питания. Высококалорийное питание с избытком быстрых углеводов, насыщенных жиров и переработанных продуктов повышает нагрузку на печень и способствует развитию инсулинорезистентности. Важно включать в рацион больше клетчатки, ненасыщенных жиров и белка, ограничивая потребление сахара и трансжиров.","4. Синдром поликистозных яичников (СПКЯ) и обструктивное апноэ сна (ОСА). СПКЯ связан с нарушением гормонального баланса и инсулинорезистентностью, что повышает риск развития ожирения и метаболических нарушений. ОСА, в свою очередь, провоцирует хроническое воспаление, гипоксию и нарушения углеводного обмена, усугубляя метаболические расстройства.","5. Генетическая предрасположенность. Некоторые гены могут повышать риск развития ожирения, диабета и заболеваний печени. Если в семье были случаи НАЖБП, аутоиммунных заболеваний печени или метаболических нарушений, вероятность их развития у последующих поколений увеличивается.","6. Метаболический синдром. Этот комплекс нарушений, включающий абдоминальное ожирение, гипертензию, инсулинорезистентность и дислипидемию, является ключевым фактором риска развития сердечно-сосудистых заболеваний и поражения печени. Контроль этих факторов риска – важный шаг к сохранению здоровья. Коррекция образа жизни, сбалансированное питание, физическая активность и своевременное медицинское наблюдение помогут снизить вероятность развития серьезных метаболических нарушений."]},{type:"TITLE",src:"",imageTitle:"",paragraphs:["Алгоритм и оценка при НАЖБП у детей."]},{type:"TEXT",src:"",imageTitle:"",paragraphs:["1. Критерии скрининга. Дети старше 9 лет с ожирением и без симптомов. Измерение АЛТ (аланинаминотрансферазы) как метод скрининга.","2. Интерпретация результатов АЛТ.","АЛТ в норме и бессимптомно. Рекомендации: Диета и физическая активность. Повторный скрининг ежегодно при наличии клинических признаков.","АЛТ больше верхнего предела нормы (ВПЗ), но меньше 80 и бессимптомно. Рекомендации: Диета и физические упражнения. Повторный скрининг через 1–6 месяцев","АЛТ выше 80 или есть симптомы / красные флаги. Дополнительное обследование: Инструментальная диагностика: УЗИ печени, эластометрия, Физикальное обследование: ИМТ, артериальное давление, черный акантоз, Лабораторные исследования: Кардиометаболические маркеры (HbA1c, липидный профиль), Маркеры повреждения печени (АСТ, ГГТТ, ЩФ, билирубин, альбумин), Анализ на вторичные причины (вирусные инфекции, целиакия, гипотиреоз, аутоиммунные заболевания, болезнь Вильсона и др.).","АЛТ >2 ВПЗ, но меньше 80 в течение <3 мес. Продолжение наблюдения Повторное обследование","3. Итоговые рекомендации: Регулярный контроль АЛТ. Включение здорового питания и физической активности. Проведение углубленного обследования при наличии отклонений"]},{type:"TITLE",src:"",imageTitle:"",paragraphs:["Клинические проявления и ранняя диагностика НАЖБП у детей"]},{type:"IMAGEBLOCK",src:c,imageTitle:"",paragraphs:["1. Бессимптомное течение. На ранних стадиях  часто протекает бессимптомно, что затрудняет раннюю диагностику.","2. Гепатомегалия. Возможна гепатомегалия, обнаруживаемая при физикальном осмотре либо случайном УЗИ обследовании.","3. Общие симптомы: слабость, утомляемость и боли в правом подреберье. Желтушный синдром (при развитии осложнений)."]},{type:"TITLE",src:"",imageTitle:"",paragraphs:["Диагностика"]},{type:"TEXT",src:"",imageTitle:"",paragraphs:["Диагностика заболеваний печени и метаболических нарушений требует комплексного подхода, включающего лабораторные исследования, анализ анамнеза и физикальный осмотр. Правильная оценка состояния пациента помогает своевременно выявить возможные патологии, определить их причину и подобрать оптимальную стратегию лечения.В рамках диагностики учитываются биохимические показатели крови, маркеры инсулинорезистентности, липидный профиль, а также специфические адипокины. Важную роль играет сбор анамнеза, особенно семейного, поскольку ряд заболеваний печени может иметь наследственную предрасположенность. Кроме того, проводится физикальный осмотр, направленный на выявление внешних признаков возможных отклонений, таких как изменения кожи, индекс массы тела и признаки гепатоспленомегалии. Далее представлены основные этапы диагностики."]},{type:"IMAGEBLOCK",src:"",imageTitle:"",paragraphs:["1. Лабораторные исследования:  Глюкоза натощак,Инсулин, HOMA-IR, Биохимические анализы ( АЛТ,АСТ, ГГТП,ЩФ, билирубин и его фракции) соотношение АЛТ/АСТ, Липидный профиль (Общий холестерин, ЛПНП,ЛПВП, Триглицериды). SFRP4 - адипокин","2. Анамнез жизни:. Семейный анамнез (случаи НАЖБП в семье, аутоиммуного гепатита, болезнь Вильсона, криптогенного цирроза печени, трансплантация печени. Употребление алкоголя и наркотиков (у подростков)","3. Физикальный осмотр. 1) ИМТ, АД. 2) Тип конституции. 3) Оценка кожных покров. 4) Гепатомегалия и/ или спленомегалия"]},{type:"TITLE",src:"",imageTitle:"",paragraphs:["Инструментальная диагностика"]},{type:"IMAGEBLOCK",src:l,imageTitle:"",paragraphs:["1. Транзиторная эластография с вибрационным контролем (например: ILivTouch).","2. Эластография сдвиговой волны на основе ультразвука (например, акустического радиационного импульса).","3. Магнитно-резонансная эластография."]},{type:"TITLE",src:"",imageTitle:"",paragraphs:["УЗИ общей брюшной полости."]},{type:"IMAGEBLOCK",src:h,imageTitle:"",paragraphs:["УЗИ общей брюшной полости - метод обследования внутренних органов с помощью воздействия ультразвуковой волны.Низкая чувствительность для определения жира (<33%) низкая специфичность. Более подходит для обследования структурных поражений и образований (кист . опухолей)"]},{type:"TITLE",src:"",imageTitle:"",paragraphs:["Транзиентная эластография печени."]},{type:"IMAGEBLOCK",src:m,imageTitle:"",paragraphs:["1. Транзиентная эластография (TE, transient elastography) – это неинвазивный ультразвуковой метод диагностики, который измеряет жесткость печени.","2. Разработка технологии началась в конце 20-го века.Впервые представлена в конце 1990-х годов.","3. Первые работы по разработке данной методики принадлежат А. П. Сарвазяну (Институт биофизики АН СССР), А. А. Фазылову и И. Х. Юхананову (НИИ онкологии и радиологии Минздрава Узбекистана)"]},{type:"TITLE",src:"",imageTitle:"",paragraphs:["Основные аппараты для транзиентной эластографии."]},{type:"TEXT",src:"",imageTitle:"",paragraphs:["Транзиентная эластография – это неинвазивный метод диагностики, позволяющий оценить степень фиброза печени путем измерения ее упругости. Этот метод активно применяется в гастроэнтерологии и гепатологии, помогая выявлять заболевания печени на ранних стадиях. Среди аппаратов, использующих данный метод, особое место занимает iLivTouch.","iLivTouch – современный и мобильный аппарат для диагностики, iLivTouch – это портативный аппарат транзиентной эластографии, который отличается высокой мобильностью и удобством использования. Его ключевые преимущества:","1. Возможность проведения диагностики в различных условиях, включая выездные обследования; ","2. Компактность и простота в эксплуатации;","3. Быстрая обработка результатов;","4. Доступность по сравнению с более громоздкими стационарными системами.","Благодаря этим характеристикам iLivTouch идеально подходит для скрининговых программ и регулярного мониторинга состояния печени у пациентов. Мы рады сообщить, что в нашем медицинском учреждении установлен аппарат iLivTouch, позволяющий проводить точную и оперативную диагностику заболеваний печени. Это дает возможность нашим пациентам получить качественное обследование без инвазивных процедур, быстро и комфортно."]},{type:"TEXT",src:"",imageTitle:"",paragraphs:["НАЖБП - это растущая проблема у детей, связанная с ожирением и метаболическими нарушениями. Ранняя диагностика и профилактика имеют решающее значение. Необходимо проводить скрининг детей из группы риска и внедрять программы по здоровому образу жизни.","Запись на исследование осуществляется по телефонам с понедельника по пятницу с 08:00 до 17:00: 8(771)-103-70-02, 8(771)-103-70-03."]}]},{idArticle:"5",title:"ВИДЕОКАПСУЛЬНАЯ ЭНДОСКОПИЯ: БЕСПЛАТНО В РАМКАХ ОСМС",subtitle:"",img:y,createdAt:"26.02.2025",blocks:[{type:"TITLE",src:"",imageTitle:"",paragraphs:["Уважаемые пациенты!\n"]},{type:"TEXT",src:"",imageTitle:"",paragraphs:["«Республиканский Центр здоровья» в рамках государственной программы проводит  диагностику желудочно-кишечного тракта (ЖКТ)  «Видеокапсульную эндоскопию».","❗️Чтобы пройти обследование в рамках ОСМС, нужно получить направление в своей поликлинике (СВА), направление может выписать участковый терапевт.","Врачу необходимо указать в направлении «Видеокапсульная эндоскопия», выбрать тарификатор: С.01.036.001","ТОО «Республиканский Центр здоровья»","Адрес: г. Астана, ул.Айнакол 60","Предварительная запись после получения направления осуществляется по телефонам в рабочие дни (понедельник – пятница, с 08:00 до 17:00). Обратите внимание: график работы в праздничные дни может отличаться.","8(771)-103-70-02, 8(771)-103-70-03."]},{type:"TITLE",src:"",imageTitle:"",paragraphs:["Капсульная эндоскопия"]},{type:"TEXT",src:"",imageTitle:"",paragraphs:["Это исследование желудочно-кишечного тракта с помощью специальной капсулы со встроенной видеокамерой, которую пациент проглатывает как обычную таблетку. Капсула самостоятельно передвигается по желудочно-кишечному тракту и выводится из организма естественным путем. Предназначена только для одноразового использования. Встроенная камера во время движения по желудочно-кишечному тракту выполняет снимки с частотой 2 кадра в секунду. Снимки передаются на записывающее устройство, закрепленное на поясе пациента. Для того чтобы результаты обследования были максимально точными, а проведение процедуры - безопасным, к капсульной эндоскопии нужно тщательно подготовиться."]},{type:"TITLE",src:"",imageTitle:"",paragraphs:["ИСТОРИЯ СОЗДАНИЯ"]},{type:"IMAGEBLOCK",src:p.p+"f1521fb6b9ad143cbce0b549da0485c6.png",imageTitle:"",paragraphs:["1990-е годы – идея и разработка.Изобретение видеокапсулы принадлежит израильскому инженеру Гавриэлю Идуану (Gavriel Iddan). Работал в оборонной промышленности, разрабатывая миниатюрные системы камер для военных целей. В 1994 году  предложил использовать похожую технологию для медицинских исследований желудочно-кишечного тракта.","2000 год – первое клиническое применение. В начале 2000-х годов видеокапсула была впервые протестирована на пациентах. В 2001 году капсульная эндоскопия получила одобрение FDA (Управления по контролю за продуктами и лекарствами США) для клинического использования.","2001-2003 годы – распространение технологии. Метод начал активно применяться в гастроэнтерологии. Видеокапсулы стали незаменимыми для диагностики болезней тонкого кишечника, включая скрытые кровотечения, болезнь Крона и опухоли."]},{type:"TITLE",src:"",imageTitle:"",paragraphs:["CТРУКТУРА КАПСУЛЫ"]},{type:"IMAGEBLOCK",src:p.p+"8ae0ad8eade21fafb98d51661b055b18.png",imageTitle:"",paragraphs:["1. КАМЕРА. Встроенная камера высокой четкости","2. ИСТОЧНИК СВЕТА. LED обеспечивает освещение","3. ПЕРЕДАТЧИК. Беспроводной передатчик данных.","4. ПИТАНИЕ. Элемент питания обеспечивает работу 8-12 часов."]},{type:"TITLE",src:"",imageTitle:"",paragraphs:["ПОДГОТОВКА К ПРОЦЕДУРЕ"]},{type:"TITLE",src:"",imageTitle:"",paragraphs:["Пациенту следует строго соблюдать все правила подготовки к исследованию!"]},{type:"IMAGEBLOCK",src:p.p+"c01b07f390dbeb927aeea7b2e4cb2f3a.png",imageTitle:"",paragraphs:["1. Исключение приема препарата железа за 5 дней.","2. Соблюдение диета за 3-5 дней до процедуры.","3. Прием слабительного препарата за день ВКЭ.","4. Исключение приема воды и пищи за 12 часов ВКЭ."]},{type:"TITLE",src:"",imageTitle:"",paragraphs:["Исследование проходит в абсолютно комфортном состоянии:"]},{type:"IMAGEBLOCK",src:d,paragraphs:[],imageTitle:"1. Пациент проглатывает видеокапсулу, размером с обычную пилюлю."},{type:"IMAGEBLOCK",src:f,paragraphs:[],imageTitle:"2. В процессе её прохождения по желудочно-кишечному тракту, устройство делает 2 снимка в секунду и передает информацию на считывающее устройство, которое прикреплено к телу пациента."},{type:"IMAGEBLOCK",src:n,paragraphs:[],imageTitle:"3. Исследование записывается в течение 9-10 часов."},{type:"IMAGEBLOCK",src:I,paragraphs:[],imageTitle:"4. После исследования капсула выходит безболезненно, естественным путем, а пациент приходит в клинику со считывающим устройством."},{type:"IMAGEBLOCK",src:L,paragraphs:[],imageTitle:"5. Далее врач анализирует полученные снимки в специальной программе и дает заключение."},{type:"TITLE",src:"",imageTitle:"",paragraphs:["Почему этот метод максимально эффективен?"]},{type:"TEXT",src:"",imageTitle:"",paragraphs:["Он превосходит возможности диагностики поверхностных поражений слизистой оболочки ЖКТ с помощью рентгена с барием, МРТ и сцинтиграфии.","Современное оборудование с высоким разрешением позволяет детальнее рассмотреть все патологические изменения. Это становится возможным благодаря использованию специальных одноразовых капсул, которые мы заказываем для каждого пациента. Одноразовые капсулы, использование которых исключает риск заражения.","Неинвазивная технология, обтекаемая форма капсулы, простая подготовка к исследованию - все это делает процедуру максимально комфортной для пациента. Общий уровень обнаружения с помощью видеокапсульной эндоскопии (ВКЭ) составляет 50–60%. В ретроспективном исследовании 1214 пациентов аномалии были выявлены у 52%. ВКЭ влияет на тактику лечения заболеваний тонкой кишки. В исследовании: 40 врачей анализировали 98 пациентов до и после ВКЭ: 67% пациентов получили изменения в лечении, 74% врачей скорректировали план терапии на основе результатов VCE","ВКЭ выявляет источники кровотечения чаще, чем push-энтероскопия. Общий уровень выявления кровотечения с помощью ВКЭ варьируется от 30 до 70%. В мета-анализе 227 исследований (22 840 процедур) 66% случаев касались предполагаемого кровотечения, а уровень выявления составил 61%.","ВКЭ-ценный инструмент диагностики, влияющий на клинические решения и причин кровотечений тонкой кишки, особенно при железодефицитной анемии. Особенно полезен при неясных кровотечениях, болезни Крона и подозрении на опухоли. Однако отсутствие возможности биопсии и необходимость исключения кишечной непроходимости перед процедурой ограничивают его применение."]},{type:"TITLE",src:"",imageTitle:"",paragraphs:["ПОКАЗАНИЯ"]},{type:"TEXT",src:"",imageTitle:"",paragraphs:["1. Устройство предназначено для диагностики заболеваний ЖКТ.","2. Боли не ясного генеза при нормальных результатах гастро - и колоноскопии.","3. Железодефицитная анемия неустановленного генеза","4. Энтериты","5. НПВП- энтеропатия","6. Болезнь Крона","7. Опухоли и полипы тонкой кишки","8. Целиакия","9. Наследственные полипозные синдромы (синдром Пейтца-Егерса).","10. При отказе пациента и других проблемных моментов  проведения ФГДС и колоноскопии."]},{type:"TITLE",src:"",imageTitle:"",paragraphs:["ПРОТИВОПОКАЗАНИЯ"]},{type:"TEXT",src:"",imageTitle:"",paragraphs:["АБСОЛЮТНЫЕ:","1. Стриктуры (сужение) кишечника после операции Установленный стеноз или обструкция кишечника","2. Выраженные нарушения моторики псевдообструкции Необходимость проведения МРТ Возраст до 18 лет","ОТНОСИТЕЛЬНЫЕ:","1. Дисфагия","2. Беременность","3. Наличие имплантируемого кардиостимулятора или дефибрилятора","4. Пациенты с высоким риском осложнений."]},{type:"TITLE",src:"",imageTitle:"",paragraphs:["ЧТО ПОКАЖЕТ ОБСЛЕДОВАНИЕ?"]},{type:"TEXT",src:"",imageTitle:"",paragraphs:["В рамках обследования могут быть выявлены такие патологические состояния, как:","1. Острый и хронический гастрит","2. Язвы слизистых оболочек","3. Эрозии","4. Язвенный колит","5. Болезнь Крона","6. Целиакия","7. Полипы желудка и кишечника (как одиночные, так и множественные, доброкачественные и злокачественные)","8. Карциноид (опухоль тонкого кишечника"]},{type:"TEXT",src:"",imageTitle:"",paragraphs:["Наш центр стремится обеспечить высокий уровень медицинской помощи и комфортные условия для наших пациентов. Мы уделяем особое внимание индивидуальному подходу к каждому человеку и предоставлению комплексных решений для достижения оптимальных результатов в лечении и восстановлении здоровья."]},{type:"TITLE",src:"",imageTitle:"",paragraphs:["Мы единственные в РК, кто проводит видеокапсульную эндоскопию по программе ОСМС."]},{type:"TEXT",src:"",imageTitle:"",paragraphs:["Этот инновационный метод позволяет безопасно и подробно исследовать состояние желудочно-кишечного тракта без дискомфорта для пациента. Запишитесь на диагностику и получите точные результаты!"]},{type:"TEXT",src:"",imageTitle:"",paragraphs:["Запись на исследование осуществляется по телефонам с понедельника по пятницу с 08:00 до 17:00: 8(771)-103-70-02, 8(771)-103-70-03."]}]},{idArticle:"4",title:"ДНЕВНОЙ СТАЦИОНАР: КОМПЛЕКСНАЯ ДИАГНОСТИКА И ЛЕЧЕНИЕ",subtitle:"В этой статье мы рассмотрим основные аспекты комплексной диагностики и лечения, предлагаемые нашим центром.",img:s,createdAt:"14.05.2024",blocks:[{type:"IMAGE",src:b,paragraphs:[],imageTitle:"Дневные стационары обеспечивают пациентам возможность получить комплексное лечение и диагностику, не пребывая в стационаре на ночь, что способствует более комфортному и эффективному восстановлению."},{type:"TEXT",src:"",imageTitle:"",paragraphs:["Республиканский центр здоровья представляет современные методы диагностики и лечения в рамках своего дневного стационара.","В этой статье мы рассмотрим основные аспекты комплексной диагностики и лечения, предлагаемые нашим центром."]},{type:"TEXT",src:"",imageTitle:"",paragraphs:["Комплексная диагностика в дневном стационаре","Для определения стратегии лечения мы проводим комплексную диагностику, которая включает в себя:","1. Лабораторные исследования: Мы предлагаем широкий спектр лабораторных анализов, направленных на выявление патологий и оценку функционального состояния организма. Это включает анализ крови, мочи, фекалий и другие специализированные тесты.","2. Инструментальные методы исследования: Наши специалисты используют современное оборудование для проведения различных инструментальных исследований, таких как УЗИ, рентгенография, МРТ и КТ. Эти методы помогают выявить патологии внутренних органов и систем.","3. Консультации специалистов: Пациенты получают консультации врачей различных специализаций, таких как терапевт, гастроэнтеролог, эндокринолог и др. Это позволяет создать индивидуальную программу лечения, учитывая все аспекты заболевания."]},{type:"TEXT",src:"",imageTitle:"",paragraphs:["Лечение в дневном стационаре","Наш дневной стационар предлагает широкий спектр услуг по лечению различных заболеваний, включая хронический гепатит. Программы лечения включают:","1. Лечение хронического гепатита: Мы предлагаем несколько вариантов программ лечения хронического гепатита, включая краткосрочные (7 дней) и продолжительные (10 дней) курсы. Каждая программа разработана с учетом индивидуальных особенностей пациента и степени тяжести заболевания.","2. Программы здоровья и красоты: Помимо лечебных программ, мы также предлагаем программы здоровья и красоты, направленные на улучшение общего состояния организма и внешнего вида. Эти программы включают в себя такие направления, как антистресс, детоксикация, витаминный комплекс и другие, которые способствуют улучшению метаболизма, восстановлению после стрессовых ситуаций и поддержанию общего здоровья."]},{type:"TEXT",src:"",imageTitle:"",paragraphs:["Наш центр стремится обеспечить высокий уровень медицинской помощи и комфортные условия для наших пациентов. Мы уделяем особое внимание индивидуальному подходу к каждому человеку и предоставлению комплексных решений для достижения оптимальных результатов в лечении и восстановлении здоровья."]},{type:"TEXT",src:"",imageTitle:"",paragraphs:["Запись на исследование осуществляется по телефонам с понедельника по пятницу с 08:00 до 17:00: 8(771)-103-70-02, 8(771)-103-70-03."]}]}]},3766:(a,e,p)=>{p.d(e,{F:()=>g});var r=p(5893),t=p(7294),i=p(5998),T=p(5461),g=function(a){var e=a.children,p=a.removeAfterUnmound,g=a.reducers,s=(0,i.oR)(),c=(0,T.T)();return(0,t.useEffect)((function(){if(Object.entries(g).forEach((function(a){var e=a[0],p=a[1];s.reducerManager.add(e,p),c({type:"@INIT"})})),p)return function(){Object.entries(g).forEach((function(a){var e=a[0];a[1],s.reducerManager.remove(e),c({type:"@UNMOUNT"})}))}}),[]),(0,r.jsx)(r.Fragment,{children:e})}}}]);